const prev=document.querySelector(".btn-prev"),next=document.querySelector(".btn-next"),slides=document.querySelectorAll("#img"),dots=document.querySelectorAll(".dot"),caruselWrapper=document.querySelectorAll("#slider");function Slider(t,e,n,c,r){this.lctrl=t,this.rctrl=e,this.ind=n,this.display=c,this.current=0,this.data=r;let s=this;this.drawIndicator=()=>{s.ind.innerHTML='<span class="dot active">'+(s.current+1+"").padStart(2,"0")+"</span>/"+(s.data.length+"").padStart(2,"0")},this.rctrl.addEventListener("click",()=>{s.display.children[s.current].classList.remove("active"),s.current==s.data.length-1?s.current=0:s.current++,s.display.children[s.current].classList.add("active"),s.drawIndicator()}),this.lctrl.addEventListener("click",()=>{s.display.children[s.current].classList.remove("active"),0==s.current?s.current=s.data.length-1:s.current--,s.display.children[s.current].classList.add("active"),s.drawIndicator()}),this.drawIndicator()}let headerSlider=new Slider(prev,next,dots[0].parentNode,caruselWrapper[0],[1,2,3]);const menuButton=document.getElementById("menuBtn"),nav=document.querySelector(".navigation"),shopBtn=document.getElementById("shopBtn"),shopWindow=document.querySelector(".shop-window"),closeBtn=document.querySelector(".navigation__head-close");menuButton.onclick=()=>{nav.classList.contains("active")?nav.classList.remove("active"):nav.classList.add("active")},closeBtn.onclick=()=>{nav.classList.contains("active")&&nav.classList.remove("active")},shopBtn.onclick=()=>{shopWindow.classList.contains("active")?shopWindow.classList.remove("active"):shopWindow.classList.add("active")};